import base64, struct
bmp_data = base64.b64decode('')
def bmp_info(data):
    l = struct.unpack('<ccIIIIIIHH',data[:30])
    if l[0]==b'B' and l[1]==b'M':
        print('是位图')
    else:
        print('不是位图')
    return {
        'width': l[6],
        'height': l[7],
        'color': l[9]
    }

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
print(bi['width'])
assert bi['height'] == 10
print(bi['height'])
assert bi['color'] == 16
print(bi['color'])
print('ok')